//интерфейс нужен для того что бы обозначить правила классов, которые наследуются от него, какие методы необходимо реализовать 
interface PropertyInterface {
	function AddProperty($propertyName, $value); //создать свойство с данными
	function DeleteProperty($propertyName); //удалить свойство 
	function GetProperty($propertyName); //получить данные из свойства 
	function SetProperty($propertyName, $value); //обновить данные в свойстве 
	
}

//класс контейнер наследуется от интерфейса для реализации логики по правилам интерфейса
class PropertyContainer extends PropertyInterface {
	private $propertyContainer = [];
	
	public function AddProperty($propertyName, $value) {
		$this->propertyContainer[$propertyName] = $value;
	}
	
	public function DeleteProperty($propertyName) {
		unset(propertyContainer[$propertyName]);
	}
	
	public function GetProperty($propertyName) {
		return $this->propertyContainer[$propertyName] ?? null;
	}
	
	public function SetProperty($propertyName, $value) {
		if(!isset($this->propertyContainer[$propertyName])) {
			throw new Exception("Property [{$propertyName}] is not found");
		}
		$this->propertyContainer[$propertyName] = $value;
	}
}


