//ну для начала реализуем интерфейс, по которому нужно будет реализовывать наследуюемые классы
interface MessengerInterface {
	//установить отправителя
	public function setSender($value): MessengerInterface;

	//установить получателя
	public function setRecipient($value): MessengerInterface;

	//установить сообщение
	public function setMessage($value): MessengerInterface;

	//отправить сообщение
	public function send():bool; 
}

//Абстрактный класс, который реализовывает базовые методы интерфейса
//последующие классы наследуются от абстрактного класса, для дальнейшей реализации
abstract class AbstractMessenger implements MessengerInterface {
	protected $sender; //отправитель

	protected $recipient; //получатель

	protected $message; //сообщение 

	//установить отправителя
	public function setSender($value) : MessengerInterface
	{
		$this->sender = $value;
		return $this;
	}

	//установить получателя
	public function setRecipient($value): MessengerInterface
	{
		$this->recipient = $value;
		return $this;
	}

	public function setMessage($value): MessengerInterface
	{
		$this->message = $value;
		return $this;
	}

	//отправить сообщение
	public function send(): bool 
	{
		return true;
	}
}

//т.к. мы наследуемся от абстрактного класса, то метод send() будет перегружен,
//поэтому, мы отправляем сенд на абстрактный класс через parrent
//
class EmailMessenger extends AbstractMessenger {
	//тут много кода по реализации отправки по email
	public function send(): bool 
	{
		//тут код отправки
		echo 'Send by Email<br>';
		return parent::send(); //отправляем из абстрактного класса
	}
}

class SmsMessenger extends AbstractMessenger {
	//тут много кода реализации отправки по СМС
	public function send(): bool 
	{
		//тут код отправки
		echo 'Send by SMS<br>';
		return parent::send(); //отправляем из абстрактного класса
	}
}

//сам класс который будет делегировать 
class AppMessenger implements MessengerInterface {
	
	//приватное свойство messenger, это тот, на кого мы будем делегировать работу (экземпляр класса)
	private $messenger; 

	public function __construct(){
		//по дефолту вызывем метод toEmail где у нас создаётся класс EmailMessenger и присваивается в messenger переменную
		//это нужно чтоб ничто не сломалось
		$this->toEmail(); 
	}

	static public function getDescription() {

	}

	//если по email, то вернём экземпляр класса 
	//EmailMessenger и ещё присвоим его в переменную messenger
	public function toEmail(){ 
		$this->messenger = new EmailMessenger();
		return $this;
	}


	//если по sms, то вернём экземпляр класса
	//SmsMessenger и ещё присвоим его в переменную messenger
	public function toSMS(){
		//т.к. у нас изначально toEmail, то тут мы просто перезапишем в messenger новый экземпляр класса SmsMessenger
		$this->messenger = new SmsMessenger();
		return $this;
	}

	//т.к. у нас данный класс так же наследуется от интерфейса, то нам необходимо реализовать его методы
	public function setSender($value): MessengerInterface { //обязательно возвращаемый у нас будет MessengerInterface
		$this->messenger->setSender($value); //но тут мы будем делегировать методы в тот класс, который породили в messenger
		return $this->messenger;
	}

	//т.к. у нас данный класс так же наследуется от интерфейса, то нам необходимо реализовать его методы
	public function setRecipient($value): MessengerInterface { //обязательно возвращаемый у нас будет MessengerInterface
		$this->messenger->setRecipient($value); //но тут мы будем делегировать методы в тот класс, который породили в messenger
		return $this->messenger;
	}
	
	//т.к. у нас данный класс так же наследуется от интерфейса, то нам необходимо реализовать его методы
	public function setMessage($value): MessengerInterface {
		$this->messenger->setMessage($value); //но тут мы будем делегировать методы в тот класс, который породили в messenger
		return $this->messenger;
	}


	//т.к. у нас данный класс так же наследуется от интерфейса, то нам необходимо реализовать его методы
	public function send(): bool {
		return $this->messenger->send(); //но тут мы будем делегировать методы в тот класс, который породили в messenger
	}

}

