//каналов может быть много, поэтому везде делаем стандартизированные интерфейсы
//для того чтоб, при добавлении нового издателя, просто всё работало без изменения кода 
//в других классах


/**
 * Интерфейс канала событий
 * связующее звено между подписчиками и издателями
 */
interface EventChannelInterface {
	/**
	 * Издатель уведомленяет канал о том что надо
	 * всех кто подписан на тему $topic умедомить данными $data
	 * 
	 * @param  [string] $topic [топик канала]
	 * @param  [mixed] $data  [данные]
	 * @return [mixed]        [description]
	 */
	public function publish($topic, $data);


	/**
	 * подписчик $subscriber подписывается на событие (данные, инфу и тп) $topic
	 * 
	 * @param  [string]              $topic      [топик канала]
	 * @param  SubscriberInterface $subscriber [подписчик]
	 * @return [mixed]                          [description]
	 */
	public function subscribe($topic, SubscriberInterface $subscriber);
}

class EventChannel implements EventChannelInterface {

	//тут хранятся все издатели (Publisher) и подписчика на событие (Subscribers)
	private $topics = [];


	public function __construct(){
		echo "Был создан канал событий<br>";
	}

	/**
	 * Сам метод добавления подписчика на событие издателя в приватный массив
	 * @param  [string]              $topic      [топик канала]
	 * @param  SubscriberInterface $subscriber [подписчик]
	 * @return [mixed]                          [description]
	 */
	public function subscribe($topic, SubscriberInterface $subscriber){
		$this->topics[$topic][] = $subscriber;

		echo "{$subscriber->getName()} подписан(а) на [{$topic}]<br>";
	}

	/**
	 * Триггер на уведомление подписчиков от издателей, вот этот метод как раз и есть канал событий
	 * @param  [string] $topic [топик канала]
	 * @param  [string] $data  [данные какие хотим чо сюда хз]
	 * @return [mixed]        [description]
	 */
	public function publish($topic, $data) {
		if(empty($this->topics[$topic])) { //если нет привязки к топику канала
			return; //то ничего не делать, сразу завершить метод
		}
		echo "Канал событий получил от издателя [{$topic}] данные [{$data}] и начал оповещать подписчиков<br>";
		//прокрутим всех подписчиков из топика
		foreach($this->topics[$topic] as $subscriber) {
			$subscriber->notify($data); //и уведомим их об событии
		}
	}
}

interface PublisherInterface {
	/**
	 * Уведомление каналу событий о своём событии
	 * далее канал уведомляет об событии подписчиков
	 *
	 * 
	 * @param  [type] $data [данные для публикции]
	 * @return [mixed]       [description]
	 */
	public function publish($data);
}

class Publisher implements PublisherInterface {
	//сам издатель не знает о существовании подписчиков на него
	/**
	 * Тут определяем топик этого издателя
	 * @var [string]
	 */
	private $topic;

	/**
	 * Тут определяем канал событий, куда будем отправлят уведомление о событии
	 * @var [EventChannelInterface]
	 */
	private $eventChannel;


	/**
	 * Во время инициализации издателя, мы должны прицепиться к каналу событий
	 * @param [string] $topic        [description]
	 * @param [EventChannelInterface] $eventChannel [канал событий]
	 */
	public function __construct($topic, $eventChannel){
		$this->topic = $topic; //такой то топик, такая то тема
		$this->eventChannel = $eventChannel;
		echo "Был создан издатель [{$this->topic}]<br>";
	}

	/**
	 * Уведомление каналу событий о своём событии
	 * далее канал уведомляет об событии подписчиков
	 *
	 * 
	 * @param  [type] $data [данные для публикции]
	 * @return [mixed]       [description]
	 */
	public function publish($data) {
		//отправляем событие в канал события, который уведомит всех кто подписан на этот топик
		echo "Издатель [{$this->topic}] отправил событие о новом посте [{$data}] каналу событий<br>";
		$this->eventChannel->publish($this->topic, $data);
	}
}


interface SubscriberInterface {

	/**
	 * Метод уведомления подписчика о событии через EventChannel
	 * @param  [string] $data [description]
	 * @return [mixed]       [description]
	 */
	public function notify($data);

	/**
	 * Получить имя подписчика на событие
	 * @return [string] [Имя подписчика]
	 */
	public function getName();

}

class Subscriber implements SubscriberInterface {
	/**
	 * Имя подписчика
	 * @var [string]
	 */
	private $name;

	public function __construct($name) {
		$this->name = $name;
	}

	/**
	 * Метод уведомления подписчика о событии через EventChannel
	 * @param  [string] $data [description]
	 * @return [mixed]       [description]
	 */
	public function notify($data) {
		echo "{$this->getName()} оповещен(а) данными [{$data}]<br>";
	}

	/**
	 * Получить имя подписчика
	 * @return [string] [имя]
	 */
	public function getName(){
		return $this->name;
	}
}


//класс который запускает работу канала событий и всё при всё
class EventChannelJob {
	//запуск тестового канала событий
	public function run() {
		//инициализация канала событий
		$newsChannel = new EventChannel(); //в нашем случае это новостной канал 

		//подключение канала событий к издателям и создание издателя
		$highgardenGroup = new Publisher('highgarden-news', $newsChannel); //группа новостей хайгардена
		$winterfellNews = new Publisher('winterfell-news', $newsChannel); //группа новостей винтерфелла
		$winterfellDaily = new Publisher('winterfell-news', $newsChannel); //группа новостей альтернативного винтерфелла 

		//создание подписчиков (получателей событий)
		$sansa = new Subscriber('Sansa Stark');
		$arya = new Subscriber('Arya Stark');
		$cersei = new Subscriber('Cersei Lannister');
		$snow = new Subscriber('Jon Snow');

		//связываем получателя с событием через канал событий
		$newsChannel->subscribe('highgarden-news', $cersei); //серсея подписывается на новости хайгардена
		$newsChannel->subscribe('winterfell-news', $sansa); // санса подписывается на новости винтерфелла
		$newsChannel->subscribe('highgarden-news', $arya); //арья подписывается на новости хайгардена
		$newsChannel->subscribe('winterfell-news', $arya); //а так же винтерфелла
		$newsChannel->subscribe('winterfell-news', $snow); //джон только на винтерфелл 

		//создаём событие у издателя, тем самым все получатели получат уведомление о том что у издателя произошло событие
		//получат уведомление с помощью канала событий, который мы добавляем к издателю
		//т.е. у нас получается 1 канал событий (1 класс), и несколько издателей (несколько классов) в которые мы добавляем тот канал событий
		$highgardenGroup->publish('New highgarden post');
		$winterfellNews->publish('New winterfell post');
		$winterfellDaily->publish('New alternative winterfell post');
	}
}

