class GuiKitFactory  {

	/**
	 * Create Abstract Factory
	 * @param  [string] $type [factory name]
	 * @return [GuiFactoryInterface]       [Factory]
	 */
	public function getFactory($type): GuiFactoryInterface {
		$factory;

		switch ($type) {
			case 'bootstrap': {
				echo 'Инициализация фабрики Bootstrap<br>';
				$factory = new BootstrapFactory();
				break;
			}
			case 'semanticui' : {
				echo 'Инициализация фабрики Semantic<br>';
				$factory = new SemanticUiFactory();
				break;
			}
			default: {
				exit("Неизвестный тип фабрики [{$type}]<br>");
				break;
			}
		}

		return $factory;
	}

	public static function getDescription(){
		return 1;
	}
}






