 *
 * суперкласс который реализовывает в себе механики работы фабрик дочерних классов
 */
abstract class AbstractForm implements FormInterface {
	
	/**
	 * В абстрактном классе есть логика генерации формы
	 * реализация фабрики
	 * @return [mixed] [result]
	 */
	public function render(){

		$guiKit = $this->createGuiKit(); //получить некий инструментарий для отрисовки  
		$name = get_class($guiKit);
		echo "Суперкласс реализовывает механику работы от дочернего класса [{$name}]<br>";
		//сгенерировать GUI формы
		$result[] = $guiKit->buildCheckBox()->draw();
		$result[] = $guiKit->buildButton()->draw();

		return $result;
	}
	
	/**
	 * Получаем инструментарий (фабрику) для рисования объектов формы 
	 * данный метод должен быть реализован в наследуемых классах
	 * @return [GuiFactoryInterface] [инструментарий (фабрика)]
	 */
	abstract function createGuiKit(): GuiFactoryInterface;
}


