//Пример REST
    //Аргументов REST можно писать хоть сколько т.к. это массив
    class CardItem {
        constructor(src, alt, subtitle, descriprion, price, parentElement, ...classes) { //Rest оператор у нас ...classes 
            this.src = src;
            this.subtitle = subtitle;
            this.descriprion = descriprion;
            this.price = price;
            this.alt = alt;
            this.transfer = 27; // актуальный курс доллара 
            this.classes = classes;
            this.changeToUAH();
            this.parentElement = document.querySelector(parentElement);
        }

        changeToUAH() { //конвертация доляров в гривны по курсу
            this.price = this.price * this.transfer;
        }

        render() {
            const divElement = document.createElement('div');
            if (this.classes.length) { //если не указали никакой аргумен в rest, будет пустой массив, поэтому проверяем на длинну массива
                this.classes.forEach(className => divElement.classList.add(className)); // перебрали rest и добавили все классы в div 
            } else {
                divElement.classList.add('menu__item');
            }
            divElement.innerHTML = `тут html код`;
            this.parentElement.append(divElement);
        }
    }

