//Пример на функциях конструкторах (ES5)
function User(name, lname, age) {
    this.name = name;
    this.age = age;
    let lastname = lname; //имитирование инкапсуляции, данная переменная небудет доступна извне 
    this.say = function () {
        console.log(`Привет ${this.name} ${lastname} ваш возраст ${this.age}`);
    };

    this.setLastName = function (newlastname) { //создали метод сеттер (именно как метод)
        //тут так же валидацию прикрутить можно и мутаторы
        lastname = newlastname;
    };

    this.getLastName = function() { //создали метод геттер (именно как метод)
        return lastname;
    };
}

const test = new User('Ras', 'Testerov', 26); //создаём объект функции конструктора 

//получим то что сейчас есть в свойствах объекта
console.log(test.name);
console.log(test.age);
console.log(test.lastname); // undefined (прямого доступа нет)
console.log(test.getLastName()); // через геттер будет

