class UserData {
    constructor(name, lname, age) {
        this.name = name;
        this.age = age;
        this._lastname = lname; 
    }

    say() {
        console.log(`Привет ${this.name} ${this._lastname} ваш возраст ${this.age}`);
    }

    set lastname(newlastname) { //создали метод сеттер (именно как сеттер)
        //тут так же валидацию прикрутить можно и мутаторы
        this._lastname = newlastname;
    }

    get lastname() { //создали метод геттер (именно как сеттер)
        return this._lastname;
    }
}
const testUser = new UserData('Ras', 'Testerov', 26); //создаём объект класса

//получим то что сейчас есть в свойствах объекта
