//Пример модуля методом приватного замыкания (автоматический)
//Замыкание, тут мы решаем проблему глобального просртанства
const number = 1;  //глобальная переменная 
//Отдельный модуль так сказать
(function(){  //анонимная самовызывающаяся функция (сразу запускается при загрузке)
    //создали собстветтую область видимости 
    let number = 2; //переменная внутри анонимной самовызвающийся функции
    console.log(number); //мы изменяем данные внутненней переменной (замкнулись)
    console.log(number+3);
}());
console.log(number); //извне вызвается переменная из глобального пространства 




//Пример модуля, объектный интерфейс (то же самое замыкание, но уже дёргать методы объекта можно)
const User = (function(){ //так же создаём анонимную самовызывающуюся функцию, но присвоим её в переменную
    const privat = function() { //внутри у нас есть метод, который выполняет определённую логику
        console.log('I am a private function');
    };
    return { sayHello: privat }; //вот тут мы возвращаем из этой функции объект, в котором имеется метод privat (то что нам нужно), и мы извне можем дёргать за него, но он не будет в глобальном пространстве 
}());

User.sayHello();

