//Создание промиса выглядит так
//resolve - это успешное выполнение кода
//reject - неуспешное 
const req = new Promise((resolve, reject) => {
    setTimeout(() => { //симуляция запроса в бэкэнд
        const product = {
            name: 'Mouse',
            order: 2000
        };
        console.log('Подготовка данных....');
        resolve(product); //тут мы вызываем функцию успешного выполнения промиса
        //в параметры выдаём то что нужно использовать дальше
    }, 2000);
});

req.then(data => { //сюда приходит дата из resolve(data);
    //так же можно внутри промиса создать новый промис и отправить его через резолв
    return new Promise((resolve, reject) => {
        setTimeout(() => { //симуляция запроса в бэкэнд
            data.status = 'ordered';
            console.log('Модифицируем...');
            reject(); //симулируем ошибку
            // resolve(data);
        }, 3000);
    });
}).then(data => { //т.к. у нас верху ретюрним промис с резолвом, то можем сразу вызвать резолв then
    data.modify = true; //ещё раз модифицируем
    return data; //возвращаем модифицированные данные (дальше резолвим)
}).then(modifydata => { //так же дальше можем передать резолв следующей цепочке
    console.log(modifydata);
}).catch(() => { //если что то пошло нет так, то вызываем в любой цепочке reject и он сразу приходит в catch  (пропуская остальные цепочки)
    console.error(`Ошибка данных`);
}).finally(() => { //после всех цепочек и обработок ошибок будет выполняться всегда финальный каллбэк  (даже при catch reject)
    console.log('Finally');
    //тут какие либо манипуляции по очистке данных форм и т.п.
});


const test = time => {
    return new Promise(resolve => { //иногда можно обойтись без reject (очень редко)
        setTimeout(() => {
            resolve();
        }, time); //запустить таймаут по time
    });
};

